               $connection_string = new mysqli("localhost", "root", "", "projet_final");
               $searchString = mysqli_real_escape_string($connection_string, trim(htmlentities($_POST['search'])));
               $searchString = "%$searchString%";
               $sql = "SELECT * FROM produit WHERE NomProduit  LIKE ?";
               $prepared_stmt = $connection_string->prepare($sql);
               $prepared_stmt->bind_param('s', $searchString);
               $prepared_stmt->execute();
               $result = $prepared_stmt->get_result();
               if ($result->num_rows === 0) 
               {
                   echo "No match found";
                   exit();
       
               } else {
                   
                   while ($row = $result->fetch_assoc()) {
                      ?> <a class="affichage_search" href="produit.php?idProduit=<?php echo $row['id'];?>"> <?php echo $row['NomProduit'] ?></a> <br> <?php
                   } 
               }
       
           } 
            catch(Exception $e)
           {
                echo 'Échec lors de la connexion : ' . $e->getMessage();
           }finally{
            $connection_string->close();
           }
